import React, { useState, useEffect } from "react";
import "./App.css";

// Import all the images
import owlImage from "./assets/owl.png";
import egg1Image from "./assets/WhatsApp_Image_2024-12-01_at_6.33.19_PM-removebg-preview.png";
import egg2Image from "./assets/WhatsApp_Image_2024-12-01_at_6.33.49_PM-removebg-preview.png";
import egg3Image from "./assets/WhatsApp_Image_2024-12-01_at_6.35.26_PM-removebg-preview.png";
import backgroundImage from "./assets/yvns_xtdx_210511.jpg";
import treeImage from "./assets/xxk3_avc4_190721-removebg-preview.png";
import bushImage from "./assets/od0a_ebuj_230706-removebg-preview.png";
import rockImage from "./assets/Cartoon_Pine_Tree_Clipart_PNG_Images__Tree_For_Cartoon_Isolated_On_White_Background__Tree__Bush__Cartoon_PNG_Image_For_Free_Download-removebg-preview.png";

function App() {
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(60);
  const [gameStarted, setGameStarted] = useState(false);

  const startGame = () => {
    setGameStarted(true);
  };

  useEffect(() => {
    if (gameStarted && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);

      return () => clearInterval(timer);
    }
    if (timeLeft === 0) {
      alert(`Game Over! Final Score: ${score}`);
      setGameStarted(false);
    }
  }, [gameStarted, timeLeft]);

  const findEgg = (points, event) => {
    setScore((prev) => prev + points);
    event.target.style.visibility = "hidden"; // Hide the clicked egg
  };

  const findHidingObject = (points, event) => {
    setScore((prev) => prev + points);
    event.target.style.visibility = "hidden"; // Hide the clicked object
  };

  return (
    <div
      className="App"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Timer and Score */}
      <div className="header">
        <div id="timer">Time: {timeLeft}s</div>
        <div id="scoreboard">Score: {score}</div>
      </div>

      {/* Start Button */}
      {!gameStarted && (
        <button id="start-btn" onClick={startGame}>
          Start Game
        </button>
      )}

      {/* Owl */}
      <img
        src={owlImage}
        id="owl"
        alt="Owl"
        style={{
          position: "absolute",
          top: "10%",
          left: "10%",
          cursor: "pointer",
          zIndex: 20,
          width: "8%",
        }}
      />

      {/* Hidden Objects */}
      <img
        src={treeImage}
        className="hiding-object"
        style={{
          top: "30%",
          left: "75%",
          width: "10%",
          height: "auto",
        }}
        alt="Tree"
        onClick={(e) => gameStarted && findHidingObject(10, e)}
      />
      <img
        src={bushImage}
        className="hiding-object"
        style={{
          top: "50%",
          left: "40%",
          width: "10%",
          height: "auto",
        }}
        alt="Bush"
        onClick={(e) => gameStarted && findHidingObject(15, e)}
      />
      <img
        src={rockImage}
        className="hiding-object"
        style={{
          top: "70%",
          left: "20%",
          width: "10%",
          height: "auto",
        }}
        alt="Rock"
        onClick={(e) => gameStarted && findHidingObject(20, e)}
      />

      {/* Easter Eggs */}
      <img
        src={egg1Image}
        className="object"
        style={{
          top: "35%",
          left: "77%",
          width: "6%",
          height: "auto",
        }}
        onClick={(e) => findEgg(10, e)}
        alt="Egg 1"
      />
      <img
        src={egg2Image}
        className="object"
        style={{
          top: "55%",
          left: "42%",
          width: "6%",
          height: "auto",
        }}
        onClick={(e) => findEgg(20, e)}
        alt="Egg 2"
      />
      <img
        src={egg3Image}
        className="object"
        style={{
          top: "75%",
          left: "22%",
          width: "6%",
          height: "auto",
        }}
        onClick={(e) => findEgg(15, e)}
        alt="Egg 3"
      />
    </div>
  );
}

export default App;
